mapper.select:
SELECT 

FROM business_circle a

WHERE a.id = #{id}

SELECT 

FROM business_circle a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.province = #{province}


		AND a.city = #{city}


		AND a.region = #{region}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM business_circle a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE business_circle SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
