mapper.select:
SELECT 

FROM business_market_floor a

WHERE a.id = #{id}

SELECT 

FROM business_market_floor a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.floor = #{floor}


		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM business_market_floor a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE business_market_floor SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

UPDATE business_market_floor SET
	del_flag = 1
WHERE market_id = #{marketId}
